DROP TABLE IF EXISTS `section_moderator`;
CREATE TABLE `section_moderator`  (
  `id` int NOT NULL AUTO_INCREMENT COMMENT '记录ID',
  `section_id` int NOT NULL COMMENT '版区ID',
  `user_id` int NOT NULL COMMENT '用户ID',
  `appoint_time` datetime NOT NULL COMMENT '任命时间',
  `appoint_by` int NOT NULL COMMENT '任命人ID',
  `status` int NOT NULL DEFAULT 1 COMMENT '状态（0-停用，1-正常）',
  `remark` varchar(200) CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NULL DEFAULT NULL COMMENT '备注说明',
  PRIMARY KEY (`id`) USING BTREE,
  UNIQUE INDEX `idx_section_user`(`section_id` ASC, `user_id` ASC) USING BTREE,
  INDEX `idx_user_id`(`user_id` ASC) USING BTREE,
  INDEX `idx_appoint_by`(`appoint_by` ASC) USING BTREE,
  CONSTRAINT `fk_moderator_appoint` FOREIGN KEY (`appoint_by`) REFERENCES `user` (`user_id`) ON DELETE CASCADE ON UPDATE RESTRICT,
  CONSTRAINT `fk_moderator_section` FOREIGN KEY (`section_id`) REFERENCES `section` (`section_id`) ON DELETE CASCADE ON UPDATE RESTRICT,
  CONSTRAINT `fk_moderator_user` FOREIGN KEY (`user_id`) REFERENCES `user` (`user_id`) ON DELETE CASCADE ON UPDATE RESTRICT
) ENGINE = InnoDB AUTO_INCREMENT = 7 CHARACTER SET = utf8mb4 COLLATE = utf8mb4_0900_ai_ci COMMENT = '版主表' ROW_FORMAT = Dynamic;

